import React, { useEffect, useState } from 'react';
import api from '../api'; // Axios instance setup
import { useNavigate } from 'react-router-dom';
import '../styles/dashboard.scss';  
import '../styles/app.scss';  

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login'); // Redirect to login if no token
        return;
      }

      try {
        // Fetch user profile with token
        const response = await api.get('/user/profile', {
          headers: { Authorization: `Bearer ${token}` },  // Attach token in the Authorization header
        });
        setUser(response.data);
      } catch (error) {
        console.error(error);
        alert('Failed to fetch user details');
      }
    };

    fetchUser();
  }, [navigate]);

  return (
    <div className="dashboard-container"> {/* Apply .dashboard-container */}
      <h2>Dashboard</h2>
      {user ? (
        <div className="user-info"> {/* Apply .user-info */}
          <p>Name: {user.name}</p>
          <p>Email: {user.email}</p>
          <p>Role: {user.role}</p>
          {/* Add profile image */}
          <img src={user.profileImage || 'default-profile.jpg'} alt="Profile" className="profile-image" />
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Dashboard;
